import { moments, users, events } from "./data/local";

const App: React.FC = () => {
  const [curPage, setPage] = useState(
    window.location.hash.length > 0
      ? window.location.hash.replace("#", "")
      : "index"
  );
  window.addEventListener("hashchange", function () {
