            <Route path="" render={() => <MomentFeed moments={moments} />} />
            <Route
              path="profile"
              render={() =>
                isLogged ? (
                  <Profile user={users[0]} moments={moments} />
                ) : (
                  <Login login={() => setLogged(true)} />
                )
              }
            />
            <Route
              path="notifications"
              render={() => <EventFeed events={events} />}
            />
          </Switch>
          <ModalAddMoment
