import React, { useState } from "react";
import { Route, Switch } from "react-router-dom";
import { Navbar } from "./components/Navbar";
import { MomentFeed } from "./components/MomentFeed";
import { Profile } from "./components/Profile";
import { ModalAddMoment } from "./components/ModalAddMoment";
import { Login } from "./components/Login";
import { Footer } from "./components/Footer";
import { EventFeed } from "./components/EventFeed";

import { moments, users, events } from "./data/local";

const App: React.FC = () => {

  function addMomemt() {}
  function search() {}

  const [IsOpenAddMoment, setIsOpenAddMoment] = React.useState(false);

  const [isLogged, setLogged] = useState(false);

  return (
    <>
        <Navbar
          isLogged={isLogged}
          openModal={() => setIsOpenAddMoment(true)}
          search={search}
        />
        <div className="container-sm p-2vw mb-4">
          <Switch>
            <Route exact path="/" render={() => <MomentFeed moments={moments} />} />
            <Route
              path="/profile"
              render={() =>
                isLogged ? (
                  <Profile user={users[0]} moments={moments} />
                ) : (
                  <Login login={() => setLogged(true)} />
                )
              }
            />
            <Route
              path="/notifications"
              render={() => <EventFeed events={events} />}
            />
          </Switch>
          <ModalAddMoment
            modalState={{
              isOpen: IsOpenAddMoment,
              setIsOpen: setIsOpenAddMoment,
            }}
            addMoment={addMomemt}
          />
        </div>
        <Footer />
    </>
  );
};

export default App;
