            ))}
          {curPage === "notifications" && <EventFeed events={events} />}
          <ModalAddMoment
            modalState={{
              isOpen: IsOpenAddMoment,
              setIsOpen: setIsOpenAddMoment,
            }}
            addMoment={addMomemt}
          /> */}
          <Routes>
            <Route path="" element={<MomentFeed moments={moments} />} />
            <Route
              path="/profile"
              element={
                isLogged ? (
                  <Profile user={users[0]} moments={moments} />
                ) : (
                  <Login login={() => setLogged(true)} />
                )
              }
            />
            <Route
              path="/notifications"
              element={<EventFeed events={events} />}
            />
          </Routes>
        </HashRouter>
        <ModalAddMoment
          modalState={{
            isOpen: IsOpenAddMoment,
            setIsOpen: setIsOpenAddMoment,
          }}
          addMoment={addMomemt}
        />
      </div>
      <Footer />
    </>
  );
};

export default App;
